import numpy as npa = np.arange(20,30)aa[0:3]# iki boyutlu slice işlemlerim = np.random.randint(10, size = (5,5)) mm[:,0] # sütunu verir [:satır,sütun]m[1,:] # satır verir m[1,2] # belirtilen noktam[0:3,0:4]  # verilen aralıklarda sütunları ve satırları çekme## ALT Küme içinde işlem yapmak alt_m = m[1:3,2:4]alt_malt_m[0,0] = 999alt_m[1,1] = 888alt_mm# İşlemi böyle yapınca alt_m de yaptığımız işlemler m matrixini etkiledi !!!# Bu durum yapmak istediğimizde uygun ama istemiyorsak sorunlara sebep olur a = np.random.randint(100, size = (6,6))aalt_a = a[0:2,0:3].copy()alt_a[0,0] = 999alt_aa# tanımlarken en sona .copy() ekliyince kopyasını oluşturup onun üstünde işlem yapıyosun # ana arrayden bağımsızlığını oluşturduk 